import React from 'react';

class StockSelector extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      nome: '',
      codigo: '',
      precoCompra: 0,
    }
    this.stockSelection = this.stockSelection.bind(this);
  }

  stockSelection(event, stock) {
    this.setState({
      [event.target.stock.empresa]: event.target.nome,
    })
  }


  render() {
    const { stocks } = this.props;
    const { precoCompra, nome, codigoADR } = this.state;
    console.log(stocks)
    return(
      <div>
        <table>
          <tbody>
            <tr>
              <th>Nome</th>
              <th>Código</th>
              <th>Preço</th>
            </tr>
          </tbody>
          <tbody>
            {stocks.map((e) =>
              <tr onClick={() => this.stockSelection(e)} key={e.codigoADR}>
                <td name="nome" value={nome}>{e.empresa}</td>
                <td name="codigo" value={codigoADR}>{e.codigoADR}</td>
                <td name="precoCompra" value={precoCompra}>{e.preco}</td>
              </tr>
              )}
          </tbody>
        </table>
      </div>
    )
  }
}

export default StockSelector;
