import React from 'react';
import StockSelector from './StockSelector'
import MyStocks from './MyStocks'
import './Dashboard.css';

class Dashboard extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      quantGastar: 100,
      ganhoCartTotal: '',
      ganhoCart: '',
      quantidade: '',
    }
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({ quantidade: event.target.value })
  }

  render() {
    const { stocks } = this.props;
    const { quantidade, quantGastar } = this.state;
    return(
      <div className="dashboardContainer">
        <div>
        <div>{quantGastar}</div>

        </div>
        <StockSelector stocks={stocks} />
        <div>
          <div>
            <label htmlFor="escolha%">
              Porcentagem
              <input type="text" id="escolha%" onChange={this.handleChange} name="quantidade" value={quantidade} />
            </label>
          </div>
          <label>
            <button type="button" onClick={this.handleClick} >Comprar</button>
          </label>
        </div>
        <MyStocks />
      </div>

    )
  }
}

export default Dashboard;
