import React from 'react';
import StockSelector from './StockSelector'
import MyStocks from './MyStocks'
import './Dashboard.css';

class Dashboard extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      quantGastar: 100,
      ganhoCartTotal: '',
      ganhoCart: '',
      stockBought: {
        quantidade: ''
      },
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  handleChange(event) {
    const { stockBought } = this.state;
    this.setState({ [stockBought.quantidade]: event.target.value })
  }

  handleClick(obj) {
    const { stockBought } = this.state;
    this.setState({ ...stockBought, obj });
  }

  render() {
    const { stocks } = this.props;
    const { quantidade, quantGastar, ganhoCartTotal, stockBought } = this.state;
    console.log(this.state.stockBought);
    return(
      <div>
        <div>{quantGastar}</div>
        <div>{ganhoCartTotal}</div>
        <div className="dashboardContainer">
          <StockSelector click={this.handleClick} stocks={stocks} />
          <div>
            <div>
              <label htmlFor="escolha%">
                Porcentagem
                <input type="text" id="escolha%" onChange={this.handleChange} name="quantidade" value={quantidade} />
              </label>
            </div>
          </div>
          <MyStocks stockBought={stockBought} quantidade={quantidade} />
        </div>
      </div>

    )
  }
}

export default Dashboard;
