import React from 'react';

class Stocks extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      ganho: {},
    }
    this.handleEachGain = this.handleEachGain.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  count = 0
  handleEachGain(gain) {
    this.count += gain
    console.log(this.count)
  }

  handleChange(event) {
    this.setState({ ganho: event.target.value })
  }

  render() {
    const { stockBought } = this.props;
    return (
      <tbody>
        {stockBought.map((e, index) => {
          const ganhoStock = (((12 - e.precoCompra) / e.precoCompra) * 100);
          this.handleEachGain(ganhoStock)
          return (
            <tr key={e.nome}>
              <td>{e.nome}</td>
              <td>{e.codigo}</td>
              <td>{`${e.quantidade} %`}</td>
              <td>{`R$${e.precoCompra}`}</td>
              <td>12</td>
              <td name="ganhoStock" onKeyDown={this.handleChange} value={this.state.ganho.index}>{`${ganhoStock} %`}</td>
            </tr>
          )}
        )}
      </tbody>
    )
  }
}

export default Stocks;
