import React from 'react';
import StockSelector from './StockSelector'
import MyStocks from './MyStocks'
import './Dashboard.css';

class Dashboard extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      quantGastar: 100,
      ganhoCartTotal: '',
      ganhoCart: '',
      stockBought: [],
    }
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(obj) {
    const { stockBought, quantGastar } = this.state;
    this.setState({
      stockBought: [...stockBought, obj],
      quantGastar: quantGastar - obj.quantidade,
    });
  }

  render() {
    const { stocks } = this.props;
    const { quantGastar, ganhoCartTotal, stockBought } = this.state;
    console.log(this.state.stockBought);
    return(
      <div>
        <div>{quantGastar}</div>
        <div>{ganhoCartTotal}</div>
        <div className="dashboardContainer">
          <StockSelector click={this.handleClick} stocks={stocks} />
          <MyStocks stockBought={stockBought} />
        </div>
      </div>

    )
  }
}

export default Dashboard;
